// .ctnav li 

$(document).ready(function(){
   $('.ctnav li a').click(function(){
     var x = $(this).attr('href');
     var y = $(''+ x +'').offset().top;
     console.log(x);

     $('html , body').animate({scrollTop : y}, 2000);



   });


   $(window).bind('scroll',function(){
      var scrollPos = $(window).scrollTop();
      
      var home = $('#home').offset().top;
      
      var about = $('#about').offset().top;

      var services = $('#services').offset().top;

      var portfolio = $('#portfolio').offset().top;

      var testimonials = $('#testimonials').offset().top;

      var contact = $('#contact').offset().top;
    
       if (scrollPos >= home ) {
        $('.ctnav li').removeClass('active');
        $('.ctnav li:eq(0)').addClass('active');
      }

      if (scrollPos >= about ) {
        $('.ctnav li').removeClass('active');
        $('.ctnav li:eq(1)').addClass('active');
      }

      if (scrollPos >= services ) {
        $('.ctnav li').removeClass('active');
        $('.ctnav li:eq(2)').addClass('active');
      }

      if (scrollPos >= portfolio ) {
        $('.ctnav li').removeClass('active');
        $('.ctnav li:eq(3)').addClass('active');
      }

      if (scrollPos >= testimonials ) {
        $('.ctnav li').removeClass('active');
        $('.ctnav li:eq(4)').addClass('active');
      }

      if (scrollPos >= contact ) {
        $('.ctnav li').removeClass('active');
        $('.ctnav li:eq(5)').addClass('active');
      }





   


   });
});

